alert("JS is running!");

document.addEventListener("DOMContentLoaded", function () {
    console.log("MixItUp is loading..."); // Debugging: Check if script runs

    var mixer = mixitup(".portfolio-gallery", {
        selectors: {
            target: ".port-box"
        },
        animation: {
            effects: "fade scale",
            duration: 500
        }
    });

    console.log("MixItUp initialized!"); // Debugging: Confirms MixItUp is working

    // Select all filter buttons
    const fillterButtons = document.querySelectorAll(".fillter-buttons .btn");

    fillterButtons.forEach(button => {
        button.addEventListener("click", function () {
            // Remove 'active' class from all buttons
            fillterButtons.forEach(btn => btn.classList.remove("active"));
            // Add 'active' class to the clicked button
            this.classList.add("active");
            console.log("Active class added to:", this.textContent); // Debugging step
        });
    });
});


